INSERT INTO `jos_kunena_messages_text` (`mesid`, `message`) VALUES
(1, '[size=4][b]Bienvenue dans Kunena![/b][/size] \n\n Merci d''avoir choisi Kunena pour les besoins de votre communauté dans Joomla. \n\n Kunena, traduit du Swahili signifie &ldquo;parler,&rdquo; est construit par une équipe de professionnel de l''open source avec le but de fournir une solution de forum d''haute qualité, et étroitement liée avec Joomla. Kunena supporte même des component de réseaux sociaux comme Community Builder et JomSocial. \n\n\n [size=4][b]Resources additionnelles pour Kunena[/b][/size] \n\n [b]Documentation de Kunena:[/b] [url]http://www.kunena.com/docs[/url] \n\n [b]Forum de support de Kunena[/b]: [url]http://www.kunena.com/forum[/url] \n\n [b]Téléchargement de Kunena:[/b] [url]http://www.kunena.com/downloads[/url] \n\n [b]Blog de Kunena:[/b] [url]http://www.kunena.com/blog[/url] \n\n [b]Soumettez vos idées pour des fonctionnalités:[/b] [url]http://www.kunena.com/uservoice[/url] \n\n [b]Suivez Kunena sur Twitter:[/b] [url]http://www.kunena.com/twitter[/url]');
